  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 1px 2px 0px hsl(220 6% 12% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(220 6% 12% / 0.08);
  --shadow-sm: 0px 2px 4px 0px hsl(220 6% 12% / 0.06), 0px 1px 2px -1px hsl(220 6% 12% / 0.08);
  --shadow: 0px 4px 6px -1px hsl(220 6% 12% / 0.08), 0px 2px 4px -2px hsl(220 6% 12% / 0.06);
